    ServerDefinition selectedServer;
    string selectedProductId;

    void SelectServer(string productId)
    {
        selectedProductId = productId;
        selectedServer = ServerService.GetProducts().First(x => x.Id == productId);
        GetCurrentRating();
    }

    int currentRating = 0;
    int voteCount = 0;
    string voteLabel;

    void GetCurrentRating()
    {
        if(selectedServer.Ratings == null)
        {
            currentRating = 0;
            voteCount = 0;
        }
        else
        {
            voteCount = selectedServer.Ratings.Count();
            voteLabel = voteCount > 1 ? "Votes" : "Vote";
            currentRating = selectedServer.Ratings.Sum() / voteCount;
        }

        System.Console.WriteLine($"Current rating for {selectedServer.Id}: {currentRating}");
    }

    void SubmitRating(int rating)
    {
        System.Console.WriteLine($"Rating received for {selectedServer.Id}: {rating}");
        ServerService.AddRating(selectedProductId, rating);
        SelectServer(selectedProductId);
    }

